import{_ as a,o as i,c as e,O as t}from"./chunks/framework.d111296a.js";const s="/pms-doc/assets/js-xsdd1.36bc4870.png",o="/pms-doc/assets/js-xsdd2.eefcae79.png",u=JSON.parse('{"title":"销售订单","description":"","frontmatter":{},"headers":[],"relativePath":"技术管理/销售订单.md","filePath":"技术管理/销售订单.md"}'),r={name:"技术管理/销售订单.md"};function c(n,l,d,p,_,m){return i(),e("div",null,l[0]||(l[0]=[t('<h1 id="销售订单" tabindex="-1">销售订单 <a class="header-anchor" href="#销售订单" aria-label="Permalink to &quot;销售订单&quot;">​</a></h1><blockquote><p>销售订单列表位于技术部板块，销售订单里面的内容是在合同列表签章完开始生产以后带入过来的，在销售订单中可以去对某一个批次号下面的产品进行排单或者退回，状态分为：处理中、未处理、已处理（由于批次号、合同单号过长，所以展示不完整，鼠标可悬浮下方对应的批次号或者合同单号以便复制）在点击详情页面中带有小笔图标的信息均可编辑，可选择（显示或隐藏图标）在打印是是否展示页面所属的信息</p></blockquote><h4 id="_1-如图所示" tabindex="-1">1. 如图所示： <a class="header-anchor" href="#_1-如图所示" aria-label="Permalink to &quot;1. 如图所示：&quot;">​</a></h4><ul><li>销售员：鼠标悬浮在销售员对应的员工下面可显示员工的基本信息</li><li>订单附件：是指当时双方在合同列表签章时上传的附件，点击可查看、可下载</li><li>打印：如果在详情里面点击打印以后页面会提示已打印标识告诉用户这个单子已经打印过了</li><li>退回：可退回，但是在已处理完的情况下，不能退回，只能查看信息</li><li>未处理：没有排单的情况下显示未处理</li><li>处理中：点击排单以后显示 “处理中”</li><li>已处理：点击完排单以后，再次点击下方的完成显示 “已处理”</li><li>下载：可点击下载页面中的产品信息</li><li>打印：可点击打印页面中的产品信息</li><li>选择模板：在销售订单标题名称后面有选择模板的图标，点击图标可选择其他的模板（模板来源于当时存储的模板）</li><li>存为模板：点击可把当前页面信息存储为模板，用于选择模板时更换</li><li>隐藏 / 显示：在页面中有隐藏/ 显示的图标， 用于打印时是否显示页面的信息，可根据页面中使用提示查看（显示图标代表打印时可以看到，隐藏图标代表打印是不可以看到，同时可新增字段（鼠标悬浮在表格字段上方所对应名称上面会显示 隐藏、显示、新增的图标））</li></ul><p><img src="'+s+'" alt="如图所示"></p><h4 id="_2-如图所示" tabindex="-1">2. 如图所示： <a class="header-anchor" href="#_2-如图所示" aria-label="Permalink to &quot;2. 如图所示：&quot;">​</a></h4><ul><li>详情：点击详情跳转弹窗可进行查看这个产品的工艺线路和排单</li><li>完成：点击完成将流转到调度任务里面</li></ul><p><img src="'+o+'" alt="如图所示"></p>',8)]))}const f=a(r,[["render",c]]);export{u as __pageData,f as default};
