import{_ as l,a as i,b as e}from"./chunks/ddrw-cp3.47684c42.js";import{_ as t,o as p,c as r,O as o}from"./chunks/framework.623160f5.js";const b=JSON.parse('{"title":"调度任务—产品","description":"","frontmatter":{},"headers":[],"relativePath":"生产管理/生产计划/生产安排.md","filePath":"生产管理/生产计划/生产安排.md"}'),s={name:"生产管理/生产计划/生产安排.md"};function n(c,a,_,d,h,m){return p(),r("div",null,a[0]||(a[0]=[o('<h1 id="调度任务—产品" tabindex="-1">调度任务—产品 <a class="header-anchor" href="#调度任务—产品" aria-label="Permalink to &quot;调度任务—产品&quot;">​</a></h1><blockquote><p>调度任务数据来源：销售订单完成排单后，调度任务列表根据销售订单生成对应的调度任务,目前分为批次维度和产品维度。</p></blockquote><h4 id="_1-产品维度安排" tabindex="-1">1.产品维度安排 <a class="header-anchor" href="#_1-产品维度安排" aria-label="Permalink to &quot;1.产品维度安排&quot;">​</a></h4><ul><li><p>可通过批次号，产品名称，产品型号，产品交期进行数据筛选</p></li><li><p>退回功能：一个批次中的产品未进行过安排，可进行退回操作。(退回操作后数据返回到销售订单列表中，可重新进行排单)</p></li><li><p>当安排的产品有工艺路线时，可以通过库存，采购（包含采购成品和外协加工数量），生产方式进行调度安排</p></li><li><p>本次安排数量：使用库存数量+采购数量（采购成品+外协加工数量）+生产数量</p></li><li><p>待安排数量：订单数量-已安排数量</p></li><li><p>生产数量计算规则：以能满足生产的产品数量的最小配套零件进行计算</p><p>-例如：A产品生产需要配套3个B产品，2个C产品，调度安排生产时，提供了9个B产品，4个C产品，则生产数量为2个</p></li></ul><p><img src="'+l+'" alt="如图所示"></p><h4 id="_2-采购安排-外协加工明细" tabindex="-1">2.采购安排-外协加工明细 <a class="header-anchor" href="#_2-采购安排-外协加工明细" aria-label="Permalink to &quot;2.采购安排-外协加工明细&quot;">​</a></h4><ul><li>没有使用工艺路线的产品，安排采购，使用外协加工时，可手动添加外发配套零件</li></ul><p><img src="'+i+'" alt="如图所示"></p><h4 id="_3-批量安排" tabindex="-1">3.批量安排 <a class="header-anchor" href="#_3-批量安排" aria-label="Permalink to &quot;3.批量安排&quot;">​</a></h4><ul><li><p>安排：只支持相同产品的合并安排</p></li><li><p>库存：在安排中有库存的话可以使用库存来安排</p></li><li><p>采购数量：分为直接采购成品和外协加工</p><p>-外协加工：有工艺路线的产品，安排采购使用外协加工时，可直接带出来所用的零件。没有使用工艺路线的产品，安排采购，使用外协加工时，可手动添加外发配套零件</p></li><li><p>生产数量：可点击工艺路线图标选择路线安排物料（可用库存或者采购），安排完成生产数量会自动带出</p></li></ul><p><img src="'+e+'" alt="如图所示"></p>',11)]))}const q=t(s,[["render",n]]);export{b as __pageData,q as default};
