import{_ as e,o as t,c as l,O as o}from"./chunks/framework.623160f5.js";const r="/pms-doc/assets/cw-bxsq1.f823e36c.png",i="/pms-doc/assets/cw-bxsq2.945b98c4.png",m=JSON.parse('{"title":"报销申请","description":"","frontmatter":{},"headers":[],"relativePath":"财务管理/报销申请.md","filePath":"财务管理/报销申请.md"}'),s={name:"财务管理/报销申请.md"};function p(n,a,h,c,d,u){return t(),l("div",null,a[0]||(a[0]=[o('<h1 id="报销申请" tabindex="-1">报销申请 <a class="header-anchor" href="#报销申请" aria-label="Permalink to &quot;报销申请&quot;">​</a></h1><blockquote><p>&quot;报销申请列表&quot;位于财务管理板块，在&quot;报销申请列表中，可新增报销单子，新增完成以后，页面的操作区域出现：详情、负责人审批、财务审批，同时支持查看明细、附件</p></blockquote><h4 id="_1-新增报销申请" tabindex="-1">1. 新增报销申请 <a class="header-anchor" href="#_1-新增报销申请" aria-label="Permalink to &quot;1. 新增报销申请&quot;">​</a></h4><ul><li><p>点击报销申请按钮可新增申请表单</p><p>-可一次性上传多个报销类型的申请</p><p>-在报销申请中可以点击上传附件，（可上传多个附件（支持删除、下载、预览）</p></li></ul><h4 id="_2-保存草稿功能、" tabindex="-1">2.保存草稿功能、 <a class="header-anchor" href="#_2-保存草稿功能、" aria-label="Permalink to &quot;2.保存草稿功能、&quot;">​</a></h4><ul><li>可存为草稿，再次打开新增报销申请时显示之前所存的草稿</li></ul><h4 id="_3-明细" tabindex="-1">3.明细 <a class="header-anchor" href="#_3-明细" aria-label="Permalink to &quot;3.明细&quot;">​</a></h4><ul><li><p>点击明细可查看这条报销数据单的报销明细</p><p>-数字代表这个数据单中有多少个报销类型</p></li></ul><h4 id="_4-附件" tabindex="-1">4.附件 <a class="header-anchor" href="#_4-附件" aria-label="Permalink to &quot;4.附件&quot;">​</a></h4><p>*点击查看详情文字可查看当时新增报销申请时上传的文件（支持预览、下载、pdf打印）</p><p><img src="'+r+'" alt="如图所示"></p><h4 id="_5-审批" tabindex="-1">5.审批 <a class="header-anchor" href="#_5-审批" aria-label="Permalink to &quot;5.审批&quot;">​</a></h4><ul><li><p>分为负责人审批和财务审批</p><p>-点击负责人审批，需要负责人进行审批(分为 “通过” 和 “退回”，如果退回需要重新发起审批 )，审批中支持下载附件，可保存草稿（保存的草稿会在您再次点击这条数据的 “负责人审批“ 时出现）</p><p>-点击财务审批，需要财务进行审批(分为 “通过” 和 “退回”，如果退回需要重新发起审批 )，审批中支持下载附件，可保存草稿（保存的草稿会在您再次点击这条数据的 “财务审批“ 时出现）</p></li></ul><h4 id="_6-重新发起" tabindex="-1">6.重新发起 <a class="header-anchor" href="#_6-重新发起" aria-label="Permalink to &quot;6.重新发起&quot;">​</a></h4><ul><li>如果负责人审批或财务审批的时没有通过，则被退回来，需要重新发起</li></ul><p>-在原来编辑的基础上重新发起</p><h4 id="_7-转账乞讨" tabindex="-1">7.转账乞讨 <a class="header-anchor" href="#_7-转账乞讨" aria-label="Permalink to &quot;7.转账乞讨&quot;">​</a></h4><ul><li><p>在负责人和财务人员审批完成后，会进行最后的审批-转账乞讨</p><p>-发起方已经完成了支付，而接收方已经收到相应的款项。这一状态表明资金已经离开发起方的账户，并进入接收方的控制范围，且双方都已确认了资金的正常到账</p></li></ul><p><img src="'+i+'" alt="如图所示"></p>',19)]))}const q=e(s,[["render",p]]);export{m as __pageData,q as default};
