import{_ as t,o as e,c as o,O as l}from"./chunks/framework.d111296a.js";const i="/pms-doc/assets/cg-bjlb.e9d47e2a.png",_=JSON.parse('{"title":"报价列表","description":"","frontmatter":{},"headers":[],"relativePath":"采购管理/报价列表.md","filePath":"采购管理/报价列表.md"}'),r={name:"采购管理/报价列表.md"};function s(n,a,p,u,c,d){return e(),o("div",null,a[0]||(a[0]=[l('<h1 id="报价列表" tabindex="-1">报价列表 <a class="header-anchor" href="#报价列表" aria-label="Permalink to &quot;报价列表&quot;">​</a></h1><blockquote><p>&quot;报价列表”位于采购管理板块，在 &quot;报价列表&quot; 中可查看所有报价的信息（来源于需方在采购板块的”询价列表”中与供方公司新增的询价信息，分为（已成交、未成交），取决于供方的销售部“询价列表中”进行转为报价单以后显示 &quot;未成交&quot;（需要在供方的销售部 ”报价列表中点击详情里面转化为合同以后，或者在需方采购部 “报价列表详情中下单（转为合同）以后显示 ”已成交“）</p></blockquote><h4 id="_1-状态提示" tabindex="-1">1. 状态提示 <a class="header-anchor" href="#_1-状态提示" aria-label="Permalink to &quot;1. 状态提示&quot;">​</a></h4><ul><li><p>未成交：只是报价完成，没有在转换为合同情况下显示 &quot;未成交&quot;</p></li><li><p>已成交：需要在供方的销售部 ”报价列表中点击详情里面转化为合同以后，或者在需方采购部 “报价列表详情中下单（转合同）以后显示 ”已成交“</p></li></ul><h4 id="_2-分发" tabindex="-1">2.分发 <a class="header-anchor" href="#_2-分发" aria-label="Permalink to &quot;2.分发&quot;">​</a></h4><ul><li>超级用户/管理员将未指定接收询价的采购人员的报价数据，分发给某个采购（支持再次分发）</li></ul><h4 id="_3-报价详情" tabindex="-1">3.报价详情 <a class="header-anchor" href="#_3-报价详情" aria-label="Permalink to &quot;3.报价详情&quot;">​</a></h4><ul><li><p>历史报价：跟这个公司销售过这个产品，如果再次报价就会有之前的报价信息</p></li><li><p>历史成交价：跟这个公司销售过这个产品,成功以后再次销售会有之前的历史成交价</p></li><li><p>下单：未成交的情况下点击详情会显示下单按钮，点击可转换到签署合同界面(可签署成合同)</p></li></ul><p><img src="'+i+'" alt="如图所示"></p>',9)]))}const q=t(r,[["render",s]]);export{_ as __pageData,q as default};
