import{_ as l}from"./chunks/cg-mblb3.06eea662.js";import{_ as o}from"./chunks/cg-mblb5.630b8436.js";import{_ as i,o as u,c as a,O as e}from"./chunks/framework.ad6dd780.js";const q="/pms-doc/.vitepress/dist/assets/cg-mblb1.e21fc490.png",r="/pms-doc/.vitepress/dist/assets/cg-mblb2.7af9d918.png",s="/pms-doc/.vitepress/dist/assets/cg-mblb4.34ec6c82.png",g=JSON.parse('{"title":"模板列表-采购","description":"","frontmatter":{},"headers":[],"relativePath":"采购管理/模板列表.md","filePath":"采购管理/模板列表.md"}'),c={name:"采购管理/模板列表.md"};function p(n,t,_,d,m,h){return u(),a("div",null,t[0]||(t[0]=[e('<h1 id="模板列表-采购" tabindex="-1">模板列表-采购 <a class="header-anchor" href="#模板列表-采购" aria-label="Permalink to &quot;模板列表-采购&quot;">​</a></h1><blockquote><p>&quot;模板列表&quot;采购管理板块，在&quot;模板列表&quot;中新增 &quot;采购询价模板&quot; &quot;采购合同模板&quot;</p></blockquote><h4 id="_1-如图所示" tabindex="-1">1. 如图所示： <a class="header-anchor" href="#_1-如图所示" aria-label="Permalink to &quot;1. 如图所示：&quot;">​</a></h4><ul><li>新增模板：点击新增模板,输入模板的名称,选择模板的类型(分为两种:采购询价和采购合同)，填写排序值(排序值是指输入的数字越大这个文件提交完成以后在表单的排列就越靠前面</li><li>模板状态：分为可用(在编辑完提交以后) ,草稿(编辑完模板没有提交而是保存草稿)</li><li>草稿状态用法：点击草稿后面的 &quot;详情&quot; 打开文件进行提交就转换为 &quot;可用&quot; 状态了</li></ul><p><img src="'+q+'" alt="如图所示"></p><h4 id="_2-如图所示-采购询价单模板" tabindex="-1">2. 如图所示：采购询价单模板 <a class="header-anchor" href="#_2-如图所示-采购询价单模板" aria-label="Permalink to &quot;2. 如图所示：采购询价单模板&quot;">​</a></h4><blockquote><p>&quot;采购询价模板&quot;在&quot;询价列表&quot; 中新增询价单时可选用模板，可以选择供方信息、供方联系人、类型、支持修改需方、供方名称、鼠标悬浮在表字段可以添加新的&quot;字段&quot;(双击编辑字段名称),新加字段支持&quot;删除&quot; &quot;添加&quot; 表格标题右侧可拖动调整列宽,拖动下方滚动条可查看更多信息、可添加备注</p></blockquote><ul><li>供方信息：来源于采购管理,供应商列表中添加的客户信息</li><li>供方联系人：采购管理,来源于供应商列表中添加了的联系人(可添加多个联系人)</li><li>类型：来源于财务管理，汇率税率列表中添加的汇率税率信息</li></ul><p><img src="'+r+'" alt="如图所示"></p><h4 id="_3-如图所示-模板列表页面中-新增询价" tabindex="-1">3. 如图所示：模板列表页面中&quot;新增询价&quot; <a class="header-anchor" href="#_3-如图所示-模板列表页面中-新增询价" aria-label="Permalink to &quot;3. 如图所示：模板列表页面中&quot;新增询价&quot;&quot;">​</a></h4><blockquote><p>&quot;新增询价&quot;位于模板列表中添加的询价模板字段中,创建的模板可直接生成询价单转到采购管理,&quot;询价列表中&quot;,可下载打印</p></blockquote><ul><li>供方信息：来源于采购管理,供应商列表中添加的客户信息</li><li>供方联系人：采购管理,来源于供应商列表中添加了联系人(可添加多个联系人)</li><li>类型：来源于财务管理,汇率税率列表中添加的汇率税率信息</li><li>选择产品：产品来源于所添加供应商公司的产品列表中的产品（可以输入型号创建产品字段）</li><li>批量导入：可批量导入产品（先下载模板Excel进行编辑导入即可）</li><li>历史报价：跟这个公司销售过这个产品，如果再次报价就会有之前的报价信息</li><li>历史成交价：跟这个公司销售过这个产品,成功了以后再次销售会有之前的历史成交价</li><li>总计：是所添加的产品单价的总计(支持更改单价添加公式(加 , 减 , 乘(乘的是百分比也就是0.01) 输入数值)</li><li>字段更改：在添加产品信息以后可以悬浮对应的产品信息如果出现&quot;更改的图标&quot;就代表可以更改这个产品内容的信息,或者出现时双击也可以更改</li><li>存为模板：如果点击存为模板在模板中展示所存的模板</li><li>确认：如果点击确认,在询价列表显示状态未报价.</li><li>保存草稿：点击保存草稿,在报价列表显示状态为 &quot;草稿&quot;</li></ul><p><img src="'+l+'" alt="如图所示"></p><h4 id="_4-如图所示-采购合同单模板" tabindex="-1">4. 如图所示：采购合同单模板 <a class="header-anchor" href="#_4-如图所示-采购合同单模板" aria-label="Permalink to &quot;4. 如图所示：采购合同单模板&quot;">​</a></h4><blockquote><p>&quot;采购合同单模板&quot;在&quot;合同列表&quot; 中新增合同单时可选用模板，可以选择需方信息,需方联系人、语言、类型、支持修改需方、供方名称、鼠标悬浮在表字段可以添加新的&quot;字段&quot;(双击编辑字段名称),新加字段支持&quot;删除&quot;,&quot;添加&quot; 表格标题右侧可拖动调整列宽,拖动下方滚动条可查看更多信息,可添加条款内容,选择抄送对象,输入条款内容(支持 编辑,添加,删除)，在签章之前需选择交货时间确认交货地点</p></blockquote><ul><li>供方信息：来源于采购管理,供应商列表中添加的客户信息</li><li>供方联系人：采购管理,来源于供应商列表中添加了联系人(可添加多个联系人)</li><li>类型：来源于财务管理,汇率税率列表中添加的汇率税率信息</li><li>语言：可选中文和英文</li></ul><p><img src="'+s+'" alt="如图所示"></p><h4 id="_5-如图所示-模板列表页面中-新增合同单" tabindex="-1">5. 如图所示：模板列表页面中&quot;新增合同单&quot; <a class="header-anchor" href="#_5-如图所示-模板列表页面中-新增合同单" aria-label="Permalink to &quot;5. 如图所示：模板列表页面中&quot;新增合同单&quot;&quot;">​</a></h4><blockquote><p>&quot;新增合同单&quot;位于模板列表中添加的合同模板字段中,创建的模板可直接生成合同单转到采购管理,&quot;合同列表中&quot;,可下载打印</p></blockquote><ul><li>供方信息：来源于采购管理,供应商列表中添加的客户信息</li><li>供方联系人：采购管理，来源于供应商列表中添加了联系人(可添加多个联系人)</li><li>类型：来源于财务管理,汇率税率列表中添加的汇率税率信息</li><li>语言：可选中文和英文</li><li>选择产品：产品来源于所添加供应商公司的产品列表中的产品（可以输入型号创建产品字段）</li><li>批量导入：可批量导入产品（先下载模板Excel进行编辑导入即可）</li><li>历史报价：跟这个公司销售过这个产品，如果再次报价就会显示之前的报价信息</li><li>历史成交价：跟这个公司销售过这个产品,成功了后再次销售会有显示前的历史成交价</li><li>总计：是所添加的产品单价的总计(支持更改单价添加公式(加 , 减 , 乘(乘的是百分比也就是0.01) 输入数值)</li><li>字段更改：在添加产品信息后可以悬浮对应的产品信息如果出现&quot;更改的图标&quot;就代表可以更改这个产品内容的信息,或者出现时双击也可以更改</li><li>交货时间：位于合同的条款下面，双击在乙方“____”之前..</li><li>存为模板：如果点击存为模板在模板列表中展示所存的模板</li><li>签章：如果点击签章在选择合同章确认以后,在合同列表显示，（签章之前需填写交货时间）</li><li>保存：点击保存,在合同列表显示状态为 &quot;未签章&quot;，点击详情可进去签章</li></ul><p><img src="'+o+'" alt="如图所示"></p>',21)]))}const x=i(c,[["render",p]]);export{g as __pageData,x as default};
