import{_ as l,o as e,c as t,O as i}from"./chunks/framework.623160f5.js";const r="/pms-doc/assets/cg-qllb1.d10fdee8.png",o="/pms-doc/assets/cg-qllb2.94b30f7e.png",m=JSON.parse('{"title":"缺料列表","description":"","frontmatter":{},"headers":[],"relativePath":"采购管理/缺料列表.md","filePath":"采购管理/缺料列表.md"}'),s={name:"采购管理/缺料列表.md"};function h(n,a,d,u,c,p){return e(),t("div",null,a[0]||(a[0]=[i('<h1 id="缺料列表" tabindex="-1">缺料列表 <a class="header-anchor" href="#缺料列表" aria-label="Permalink to &quot;缺料列表&quot;">​</a></h1><blockquote><p>&quot;缺料列表“ 位于采购管理板块 ，所采购的任何零件物料等等都会流转到缺料列表中 ​</p></blockquote><h4 id="_1-询价" tabindex="-1">1.询价 <a class="header-anchor" href="#_1-询价" aria-label="Permalink to &quot;1.询价&quot;">​</a></h4><ul><li><p>可对这个产品/零件进行询价，点击询价打开弹窗填写询价的公司、联系人、询价数量以后确认生成询价单</p><p>-生成的询价单会带到采购板块的询价列表从而进行后面的报价，签章转为合同</p></li></ul><h4 id="_2-批量询价" tabindex="-1">2.批量询价 <a class="header-anchor" href="#_2-批量询价" aria-label="Permalink to &quot;2.批量询价&quot;">​</a></h4><ul><li><p>点击批量询价，勾选所需要询价的产品/零件（可一键勾选）勾选完成点击批量询价按钮弹出弹窗填写询价数量（存在相同产品/零件多次询价）</p><p>-所选相同的产品/零件，会划分到一起（存在多次询价)</p><p>-支持一键选择/取消</p></li></ul><h4 id="_3-批量删除" tabindex="-1">3.批量删除 <a class="header-anchor" href="#_3-批量删除" aria-label="Permalink to &quot;3.批量删除&quot;">​</a></h4><ul><li>点击批量删除，勾选所需要删除的产品/零件（可一键勾选）勾选完成点击批量删除按钮删除</li></ul><h4 id="_4-产品-零件-工序" tabindex="-1">4.产品/零件/工序 <a class="header-anchor" href="#_4-产品-零件-工序" aria-label="Permalink to &quot;4.产品/零件/工序&quot;">​</a></h4><ul><li><p>产品：采购的这个产品（点击产品下方对应的产品名称可以查看产品的详情）</p></li><li><p>零件：采购的这个零件（点击零件下方对应的零件名称可以查看零件的详情</p></li><li><p>工序：这个产品/零件当时所添加的工序（点击产品/零件下方对应的工序名称可以查看工序的详情）</p></li></ul><p><img src="'+r+'" alt="如图所示"></p><h4 id="_5-分发" tabindex="-1">5. 分发 <a class="header-anchor" href="#_5-分发" aria-label="Permalink to &quot;5. 分发&quot;">​</a></h4><ul><li>点击分发可以指定分发给某人（分发完成以后见面的采购员下面显示所分发的人员名称）</li></ul><h4 id="_6-需求详情" tabindex="-1">6.需求详情 <a class="header-anchor" href="#_6-需求详情" aria-label="Permalink to &quot;6.需求详情&quot;">​</a></h4><ul><li>需求详情：点击需求详情下方对应的图标可查看这个产品/零件的需求信息（所属产品/零件、需求总数、到货日期）</li></ul><h4 id="_7-采购详情" tabindex="-1">7.采购详情 <a class="header-anchor" href="#_7-采购详情" aria-label="Permalink to &quot;7.采购详情&quot;">​</a></h4><ul><li>采购详情：，默认不显示，只有点击询价进行报价、转合同以后才显示采购的详情（可点击查看）</li></ul><h4 id="_8-详情" tabindex="-1">8.详情 <a class="header-anchor" href="#_8-详情" aria-label="Permalink to &quot;8.详情&quot;">​</a></h4><ul><li>详情：点击详情按钮可查看这个产品/零件的详情信息</li></ul><p><img src="'+o+'" alt="如图所示"></p>',20)]))}const b=l(s,[["render",h]]);export{m as __pageData,b as default};
