import{_ as i}from"./chunks/cg-mblb3.f5370c1b.js";import{_ as a,o as p,c as t,O as e}from"./chunks/framework.d111296a.js";const o="/pms-doc/assets/cg-xjlb1.6829ef74.png",m=JSON.parse('{"title":"询价列表","description":"","frontmatter":{},"headers":[],"relativePath":"采购管理/询价列表.md","filePath":"采购管理/询价列表.md"}'),r={name:"采购管理/询价列表.md"};function s(u,l,n,c,d,h){return p(),t("div",null,l[0]||(l[0]=[e('<h1 id="询价列表" tabindex="-1">询价列表 <a class="header-anchor" href="#询价列表" aria-label="Permalink to &quot;询价列表&quot;">​</a></h1><blockquote><p>&quot;询价列表”位于&quot;销售管理板块，在&quot;询价列表&quot;中新增 &quot;询价单&quot;</p></blockquote><h4 id="_1-新增询价" tabindex="-1">1.新增询价 <a class="header-anchor" href="#_1-新增询价" aria-label="Permalink to &quot;1.新增询价&quot;">​</a></h4><ul><li><p>新增询价单包括</p><p>-直接新建询价单子</p><p>-选择之前所保存的询价单模板模板</p></li></ul><p><img src="'+o+'" alt="如图所示"></p><h4 id="_2-询价单状态" tabindex="-1">2.询价单状态 <a class="header-anchor" href="#_2-询价单状态" aria-label="Permalink to &quot;2.询价单状态&quot;">​</a></h4><ul><li><p>询价单状态 : 分为、已报价、 未报价、草稿</p><p>-未报价 : 新增询价单确认以后界面显示未成交、</p><p>-已报价：新增的询价单会带入到需方哪里，在需方销售部的 “询价单列表进行分发处理以后，显示 “已报价”状态</p><p>-草稿：新增报检单点击保存草稿</p></li></ul><h4 id="_2-删除" tabindex="-1">2.删除 <a class="header-anchor" href="#_2-删除" aria-label="Permalink to &quot;2.删除&quot;">​</a></h4><ul><li>只有草稿的状态下才能删除</li></ul><h4 id="_3-其他功能" tabindex="-1">3.其他功能 <a class="header-anchor" href="#_3-其他功能" aria-label="Permalink to &quot;3.其他功能&quot;">​</a></h4><ul><li><p>供方信息 : 来源于采购管理,供应商列表中添加的客户信息</p></li><li><p>供方联系人 : 采购管理,来源于供应商列表中添加了的联系人(可添加多个联系人)</p></li><li><p>类型 : 来源于财务管理,汇率税率列表中添加的汇率税率信息</p></li><li><p>选择产品 : 产品来源于所添加供应商公司的产品列表中的产品（可以输入型号创建产品字段）</p></li><li><p>批量导入：可批量导入产品（先下载模板Excel进行编辑导入即可）</p></li><li><p>小眼睛图标：指的是页面中在甲乙方列表中的小眼睛图标，默认全展示，可关闭或者打开（如果关闭对方将无法看到这条信息，包括打印和下载都不展示）</p></li><li><p>小笔图标：傍边带有小笔图标的文字方可编辑</p></li><li><p>下载图标：点击下载图标可下载当前页面内容</p></li><li><p>使用提示：点击使用提示可弹出弹窗，在弹窗中显示动图，可切换查看对应功能的使用（新增字段、批量修改价格、调整宽度、签章显示、签章不显示）</p></li><li><p>历史报价 : 跟这个公司销售过这个产品，如果再次报价就会有之前的报价信息</p></li><li><p>历史成交价 : 跟这个公司销售过这个产品,成功了以后再次销售会有之前的历史成交价</p></li><li><p>单价/总计小数点位数显示：根据报价类型设置的小数位数显示小数位</p><p>-（例如：报价类型设置的小数位数是2位，产品列表中单价，总价显示的金额位数是两位）</p><p>-（报价类型设置的小数位数是4位，产品列表中单价，总价显示的金额位数是4位）</p></li><li><p>总计 : 是所添加的产品单价的总计(支持更改单价添加公式(加 , 减 , 乘(乘的是百分比也就是0.01) 输入数值)</p></li><li><p>字段更改 : 在添加产品信息以后可以悬浮对应的产品信息如果出现&quot;更改的图标&quot;就代表可以更改这个产品内容的信息,或者出现时双击也可以更改</p></li><li><p>存为模板 : 如果点击存为模板在模板中展示所存的模板</p></li><li><p>确认:如果点击确认,在询价列表显示状态&quot;未报价&quot;</p></li><li><p>保存草稿 : 点击保存草稿,在报价列表显示状态为 &quot;草稿&quot;</p></li></ul><p><img src="'+i+'" alt="如图所示"></p>',12)]))}const f=a(r,[["render",s]]);export{m as __pageData,f as default};
