import{_ as e,o as l,c as t,O as r}from"./chunks/framework.623160f5.js";const o="/pms-doc/assets/bxsq.4ba7389a.png",_=JSON.parse('{"title":"报销申请","description":"","frontmatter":{},"headers":[],"relativePath":"销售管理/报销申请.md","filePath":"销售管理/报销申请.md"}'),i={name:"销售管理/报销申请.md"};function n(s,a,h,u,d,p){return l(),t("div",null,a[0]||(a[0]=[r('<h1 id="报销申请" tabindex="-1">报销申请 <a class="header-anchor" href="#报销申请" aria-label="Permalink to &quot;报销申请&quot;">​</a></h1><blockquote><p>&quot;报销申请&quot;位于销售管理板块，在页面中申请的报销信息需要得到审批，如果审批不通过需重新发起</p></blockquote><h4 id="_1-新增报销申请" tabindex="-1">1. 新增报销申请 <a class="header-anchor" href="#_1-新增报销申请" aria-label="Permalink to &quot;1. 新增报销申请&quot;">​</a></h4><ul><li><p>点击报销申请按钮可新增申请表单</p><p>-如果没有报销申请的权限页面是不展示新增报销申请的按钮的，需联系运营管理员，到基础设置的流程权限管理中去设置报销申请的权限才能添加申请</p><p>-可一次性上传多个报销类型的申请</p><p>-在报销申请中可以点击上传附件，（可上传多个附件（支持删除、下载、预览）</p></li></ul><h4 id="_2-保存草稿功能、" tabindex="-1">2.保存草稿功能、 <a class="header-anchor" href="#_2-保存草稿功能、" aria-label="Permalink to &quot;2.保存草稿功能、&quot;">​</a></h4><ul><li>再次打开新增报销申请时显示之前所存的草稿</li></ul><h4 id="_2-审批" tabindex="-1">2.审批 <a class="header-anchor" href="#_2-审批" aria-label="Permalink to &quot;2.审批&quot;">​</a></h4><ul><li>分为负责人审批、总经理审批</li></ul><p>-需要有这个审权限的人才能审批(可到基础设置-流程权限管理中设置审批权限)</p><h4 id="_3-重新发起" tabindex="-1">3.重新发起 <a class="header-anchor" href="#_3-重新发起" aria-label="Permalink to &quot;3.重新发起&quot;">​</a></h4><ul><li>如果负责人或者总经理审批的时候没有通过，则需要重新发起</li></ul><p>-在原来编辑的基础上重新发起</p><p>-前提是需要有这个审权限的人才能重新发起(可到基础设置-流程权限管理中设置审批权限)</p><h4 id="_4-明细" tabindex="-1">4.明细 <a class="header-anchor" href="#_4-明细" aria-label="Permalink to &quot;4.明细&quot;">​</a></h4><ul><li>点击明细可查看这条报销数据单的报销明细 -数字代表这个数据单中有多少个报销类型</li></ul><h4 id="_5-附件" tabindex="-1">5.附件 <a class="header-anchor" href="#_5-附件" aria-label="Permalink to &quot;5.附件&quot;">​</a></h4><ul><li>点击查看详情文字可查看当时新增报销申请时上传的文件（支持预览、下载、pdf打印）</li></ul><p><img src="'+o+'" alt="如图所示"></p>',18)]))}const m=e(i,[["render",n]]);export{_ as __pageData,m as default};
