import{_ as t}from"./chunks/mblb3.3f1d591d.js";import{_ as i}from"./chunks/mblb5.87fc9ef4.js";import{_ as o,o as u,c as a,O as e}from"./chunks/framework.ad6dd780.js";const q="/pms-doc/.vitepress/dist/assets/mblb1.94822a4e.png",r="/pms-doc/.vitepress/dist/assets/mblb2.7c008297.png",s="/pms-doc/.vitepress/dist/assets/mblb4.b0df94a8.png",x=JSON.parse('{"title":"模板列表","description":"","frontmatter":{},"headers":[],"relativePath":"销售管理/模板列表.md","filePath":"销售管理/模板列表.md"}'),c={name:"销售管理/模板列表.md"};function p(n,l,_,d,m,h){return u(),a("div",null,l[0]||(l[0]=[e('<h1 id="模板列表" tabindex="-1">模板列表 <a class="header-anchor" href="#模板列表" aria-label="Permalink to &quot;模板列表&quot;">​</a></h1><blockquote><p>&quot;模板列表&quot;销售管理板块，在&quot;模板列表&quot;中新增 &quot;销售合同模板&quot; &quot;销售报价模板&quot;</p></blockquote><h4 id="_1-如图所示" tabindex="-1">1. 如图所示： <a class="header-anchor" href="#_1-如图所示" aria-label="Permalink to &quot;1. 如图所示：&quot;">​</a></h4><ul><li>新增板块：点击新增模板,输入模板的名称,选择模板的类型(分为两种:销售报价和销售合同),填写排序值(排序值是指输入的数字越大这个文件提交完成以后在表单的排列就越靠前面</li><li>模板状态：分为可用(在编辑完提交以后) ,草稿(编辑完模板没有提交而是保存草稿)</li><li>草稿箱用法：点击草稿后面的 &quot;详情&quot; 打开文件进行提交就转换为 &quot;可用&quot; 状态了</li></ul><p><img src="'+q+'" alt="如图所示"></p><h4 id="_2-如图所示-销售报价单模板" tabindex="-1">2. 如图所示：销售报价单模板 <a class="header-anchor" href="#_2-如图所示-销售报价单模板" aria-label="Permalink to &quot;2. 如图所示：销售报价单模板&quot;">​</a></h4><blockquote><p>&quot;销售报价单模板&quot;在&quot;报价列表&quot; 中新增报价单时，可选用模板、可以选择需方信息、需方联系人、语言、类型、等级、支持修改需方、供方名称，鼠标悬浮在表字段可以添加新的&quot;字段&quot;(双击编辑字段名称),新加字段支持&quot;删除&quot;,&quot;添加&quot; 表格标题右侧可拖动调整列宽,拖动下方滚动条可查看更多信息,可添加条款内容,选择抄送对象,输入条款内容(支持 编辑,添加,删除)</p></blockquote><ul><li>需方信息：来源于销售管理,客户列表中添加的客户信息</li><li>需方联系人：销售管理,来源于客户列表中添加了联系人(可添加多个联系人)</li><li>语言：可选中文和英文</li><li>类型：来源于财务管理,汇率税率列表中添加的汇率税率信息</li><li>等级：来源于财务管理,产品等级列表中添加的产品等级信息</li></ul><p><img src="'+r+'" alt="如图所示"></p><h4 id="_3-如图所示-模板列表页面中-新增报价单" tabindex="-1">3. 如图所示：模板列表页面中&quot;新增报价单&quot; <a class="header-anchor" href="#_3-如图所示-模板列表页面中-新增报价单" aria-label="Permalink to &quot;3. 如图所示：模板列表页面中&quot;新增报价单&quot;&quot;">​</a></h4><blockquote><p>&quot;新增报价单&quot;位于模板列表中添加的报价模板字段中,创建的模板可直接生成报价单转到销售管理,&quot;报价列表&quot;中,可下载打印</p></blockquote><ul><li>需方信息：来源于销售管理,客户列表中添加的客户信息</li><li>需方联系人：销售管理,来源于客户列表中添加了联系人(可添加多个联系人)</li><li>语言：可选中文和英文</li><li>类型：来源于财务管理,汇率税率列表中添加的汇率税率信息</li><li>等级：来源于财务管理,产品等级列表中添加的产品等级信息</li><li>选择产品：产品来源于产品列表中所添加的产品（可以输入型号创建产品字段）</li><li>批量导入：可批量导入产品（先下载模板Excel进行编辑导入即可）</li><li>历史报价：跟这个公司销售过这个产品，如果再次报价就会有之前的报价信息</li><li>历史成交价：跟这个公司销售过这个产品,成功了以后再次销售会有之前的历史成交价</li><li>总计：是所添加的产品单价的总计(支持更改单价添加公式(加 , 减 , 乘(乘的是百分比也就是0.01) 输入数值)</li><li>字段更改：在添加产品信息以后可以悬浮对应的产品信息如果出现&quot;更改的图标&quot;就代表可以更改这个产品内容的信息,或者出现时双击也可以更改</li><li>存为模板：如果点击存为模板在模板中展示所存的模板</li><li>确认：如果点击确认在选择业务章确认以后,在报价列表显示状态&quot;未成交&quot;,需备案处理</li><li>保存草稿：点击保存草稿,在报价列表显示状态为 &quot;草稿&quot;</li></ul><p><img src="'+t+'" alt="如图所示"></p><h4 id="_4-如图所示-销售合同单模板" tabindex="-1">4. 如图所示：销售合同单模板 <a class="header-anchor" href="#_4-如图所示-销售合同单模板" aria-label="Permalink to &quot;4. 如图所示：销售合同单模板&quot;">​</a></h4><blockquote><p>&quot;销售合同单模板在&quot;合同列表&quot; 中新增合同单时可选用模板，可以选择需方信息,需方联系人,语言,类型,等级,支持修改需方,供方名称,鼠标悬浮在表字段可以添加新的&quot;字段&quot;(双击编辑字段名称),新加字段支持&quot;删除&quot;,&quot;添加&quot; 表格标题右侧可拖动调整列宽,拖动下方滚动条可查看更多信息,可添加条款内容,选择抄送对象,输入条款内容(支持 编辑,添加,删除)，在签章之前需选择交货时间确认交货地点</p></blockquote><ul><li>需方信息：来源于销售管理,客户列表中添加的客户信息</li><li>需方联系人：销售管理,来源于客户列表中添加了联系人(可添加多个联系人)</li><li>语言：可选中文和英文</li><li>类型：来源于财务管理,汇率税率列表中添加的汇率税率信息</li><li>等级：来源于财务管理,产品等级列表中添加的产品等级信息</li></ul><p><img src="'+s+'" alt="如图所示"></p><h4 id="_5-如图所示-模板列表页面中-新增合同单" tabindex="-1">5. 如图所示：模板列表页面中&quot;新增合同单&quot; <a class="header-anchor" href="#_5-如图所示-模板列表页面中-新增合同单" aria-label="Permalink to &quot;5. 如图所示：模板列表页面中&quot;新增合同单&quot;&quot;">​</a></h4><blockquote><p>&quot;新增合同单&quot;位于模板列表中添加的合同模板字段中,创建的模板可直接生成合同单转到销售管理,&quot;合同列表中&quot;,可下载打印</p></blockquote><ul><li>需方信息：来源于销售管理,客户列表中添加的客户信息</li><li>需方联系人：销售管理,来源于客户列表中添加了联系人(可添加多个联系人)</li><li>语言：可选中文和英文</li><li>类型：来源于财务管理,汇率税率列表中添加的汇率税率信息</li><li>等级：来源于财务管理,产品等级列表中添加的产品等级信息</li><li>选择产品：产品来源于产品列表中所添加的产品（可以输入型号创建产品字段）</li><li>批量导入：可批量导入产品（先下载模板Excel进行编辑导入即可）</li><li>历史报价：跟这个公司销售过这个产品，如果再次报价就会有之前的报价信息</li><li>历史成交价：跟这个公司销售过这个产品,成功了以后再次销售会有之前的历史成交价</li><li>总计：是所添加的产品单价的总计(支持更改单价添加公式(加 , 减 , 乘(乘的是百分比也就是0.01) 输入数值)</li><li>字段更改：在添加产品信息后可以悬浮对应的产品信息如果出现&quot;更改的图标&quot;就代表可以更改这个产品内容的信息,或者出现时双击也可以更改</li><li>交货时间：位于合同的条款下面，双击 在乙方“____”之前......</li><li>存为模板：如果点击存为模板在模板列表中展示所存的模板</li><li>签章：如果点击签章在选择合同章确认后,在合同列表显示，可开始生产（签章之前需填写交货时间）</li><li>保存：点击保存,在合同列表显示状态为 &quot;未签章&quot;，点击详情可进去签章</li></ul><p><img src="'+i+'" alt="如图所示"></p>',21)]))}const g=o(c,[["render",p]]);export{x as __pageData,g as default};
