import{_ as i}from"./chunks/mblb3.a121d9c2.js";import{_ as t}from"./chunks/mblb5.49ea9294.js";import{_ as o,o as p,c as a,O as e}from"./chunks/framework.623160f5.js";const u="/pms-doc/assets/mblb1.cac238be.png",r="/pms-doc/assets/mblb2.2464a606.png",q="/pms-doc/assets/mblb4.34c1204d.png",x=JSON.parse('{"title":"模板列表","description":"","frontmatter":{},"headers":[],"relativePath":"销售管理/模板列表.md","filePath":"销售管理/模板列表.md"}'),s={name:"销售管理/模板列表.md"};function c(n,l,m,_,d,h){return p(),a("div",null,l[0]||(l[0]=[e('<h1 id="模板列表" tabindex="-1">模板列表 <a class="header-anchor" href="#模板列表" aria-label="Permalink to &quot;模板列表&quot;">​</a></h1><blockquote><p>&quot;模板列表&quot;位于销售管理板块，可以维护个人的 &quot;销售合同模板&quot; &quot;销售报价模板&quot;</p><p>经常使用的报价信息，合同信息，可以维护成模板，方便根据模板创建报价单，合同。</p></blockquote><h4 id="_1-新增模板" tabindex="-1">1. 新增模板： <a class="header-anchor" href="#_1-新增模板" aria-label="Permalink to &quot;1. 新增模板：&quot;">​</a></h4><ul><li>输入模板的名称,选择模板的类型(分为两种:销售报价和销售合同),填写排序值：排序值是指输入的数字越大,显示和选择时就越靠前</li><li>模板状态：分为可用(在编辑完提交以后) ,草稿(编辑完模板没有提交而是保存草稿)</li><li>草稿转可用状态：点击草稿后面的 &quot;详情&quot; 打开文件进行提交就转换为 &quot;可用&quot; 状态了</li><li>删除：支持删除模板，删除时系统会提示是否确认删除</li></ul><p><img src="'+u+'" alt="如图所示"></p><h4 id="_2-销售报价单模板" tabindex="-1">2. 销售报价单模板 <a class="header-anchor" href="#_2-销售报价单模板" aria-label="Permalink to &quot;2. 销售报价单模板&quot;">​</a></h4><blockquote><p>&quot;可以选择客户、客户联系人、语言、类型、等级、支持修改需方，鼠标悬浮在产品信息表格添加新的&quot;字段&quot;,双击编辑字段名称),新加字段支持&quot;删除&quot;,&quot;添加&quot; 表格标题右侧可拖动调整列宽,拖动下方滚动条可查看更多信息,,文本框内可编辑条款内容</p></blockquote><ul><li><p>需方信息：来源于销售管理,客户列表中添加的客户信息</p><p>-显示关联图标的代表这个公司有关联的子公司</p></li><li><p>需方联系人：来源于客户列表中添加了联系人</p></li><li><p>语言：可选中文和英文</p></li><li><p>类型：来源于财务管理,汇率税率列表中添加的汇率税率信息</p></li><li><p>等级：来源于财务管理,产品等级列表中添加的产品等级信息</p></li><li><p>小眼睛图标：指的是页面中在甲乙方列表中的小眼睛图标，默认全展示，可关闭或者打开（根据模板创建的报价，双方信息中不显示的部分，直接不显示）</p></li><li><p>抄送：下方文本框内所填写的信息会发送给所勾选的抄送对象</p></li></ul><p><img src="'+r+'" alt="如图所示"></p><h4 id="_3-根据报价模板-新增报价" tabindex="-1">3. 根据报价模板，新增报价 <a class="header-anchor" href="#_3-根据报价模板-新增报价" aria-label="Permalink to &quot;3. 根据报价模板，新增报价&quot;">​</a></h4><blockquote><p>&quot;根据报价模板可直接生成报价单</p></blockquote><ul><li><p>需方信息：来源于销售管理,客户列表中添加的客户信息</p><p>-显示关联图标的代表这个公司有关联的子公司</p></li><li><p>关联公司：供需方可以通过下拉框选择关联公司，同时会根据选择的关联公司，进行信息的更换。</p><p>—使用关联公司时，进行签章时印章使用的为关联公司印章。（关联公司印章也需要设置后对人员进行授权）</p></li><li><p>需方联系人：来源于客户列表中添加了联系人</p></li><li><p>语言：可选中文和英文</p></li><li><p>类型：来源于财务管理,汇率税率列表中添加的汇率税率信息</p></li><li><p>等级：来源于财务管理,产品等级列表中添加的产品等级信息</p></li><li><p>选择产品：产品来源于产品列表中所添加的产品（可以输入型号创建产品字段）</p></li><li><p>批量导入：可批量导入产品（先下载模板Excel进行编辑导入即可）</p></li><li><p>小眼睛图标：指的是页面中在甲乙方列表中的小眼睛图标，默认全展示，可关闭或者打开（如果关闭对方将无法看到这条信息，包括打印和下载都不展示）</p></li><li><p>使用提示：点击使用提示可弹出弹窗，在弹窗中显示动图，可切换查看对应功能的使用（新增字段、批量修改价格、调整宽度、签章显示、签章不显示）</p></li><li><p>历史报价：跟这个公司销售过这个产品，如果再次报价就会有之前的报价信息</p></li><li><p>历史成交价：跟这个公司销售过这个产品,成功了以后再次销售会有之前的历史成交价</p></li><li><p>总计：是所添加的产品单价的总计(支持更改单价添加公式(加 , 减 , 乘(乘的是百分比也就是0.01) 输入数值)</p></li><li><p>字段更改：在添加产品信息以后可以悬浮对应的产品信息如果出现&quot;更改的图标&quot;就代表可以更改这个产品内容的信息,或者出现时双击也可以更改</p></li><li><p>交货时间：添加交货时间，不添加交货时间在签章时会提醒</p></li><li><p>抄送：下方文本框内所填写的信息会发送给所勾选的抄送对象</p></li><li><p>存为模板：如果点击存为模板在模板中展示所存的模板</p></li><li><p>确认：如果点击确认在选择业务章确认以后,在报价列表显示状态&quot;未成交&quot;,需备案处理</p></li><li><p>保存草稿：点击保存草稿,在报价列表显示状态为 &quot;草稿&quot;</p></li></ul><p><img src="'+i+'" alt="如图所示"></p><h4 id="_4-销售合同单模板" tabindex="-1">4. 销售合同单模板 <a class="header-anchor" href="#_4-销售合同单模板" aria-label="Permalink to &quot;4. 销售合同单模板&quot;">​</a></h4><blockquote><p>&quot;可以选择客户、客户联系人、语言、类型、等级、支持修改需方，鼠标悬浮在产品信息表格添加新的&quot;字段&quot;,双击编辑字段名称),新加字段支持&quot;删除&quot;,&quot;添加&quot; 表格标题右侧可拖动调整列宽,拖动下方滚动条可查看更多信息,,文本框内可编辑条款内容</p></blockquote><ul><li>需方信息：来源于销售管理,客户列表中添加的客户信息</li></ul><p>-显示关联图标的代表这个公司有关联的子公司</p><ul><li>需方联系人：、来源于客户列表中添加了联系人</li><li>语言：可选中文和英文</li><li>类型：来源于财务管理,汇率税率列表中添加的汇率税率信息</li><li>等级：来源于财务管理,产品等级列表中添加的产品等级信息</li><li>小眼睛图标：指的是页面中在甲乙方列表中的小眼睛图标，默认全展示，可关闭或者打开（根据模板创建的合同，双方信息中不显示的部分，直接不显示）</li><li>抄送：下方文本框内所填写的信息会发送给所勾选的抄送对象</li></ul><p><img src="'+q+'" alt="如图所示"></p><h4 id="_5-根据合同模板-新增合同" tabindex="-1">5. 根据合同模板，新增合同 <a class="header-anchor" href="#_5-根据合同模板-新增合同" aria-label="Permalink to &quot;5.  根据合同模板，新增合同&quot;">​</a></h4><blockquote><p>根据合同模板可直接生成合同单</p></blockquote><ul><li>需方信息：来源于销售管理,客户列表中添加的客户信息</li></ul><p>-显示关联图标的代表这个公司有关联的子公司</p><ul><li>需方联系人：来源于客户列表中添加了联系人</li><li>语言：可选中文和英文</li><li>类型：来源于财务管理,汇率税率列表中添加的汇率税率信息</li><li>等级：来源于财务管理,产品等级列表中添加的产品等级信息</li><li>选择产品：产品来源于产品列表中所添加的产品（可以输入型号创建产品字段）</li><li>小眼睛图标：指的是页面中在甲乙方列表中的小眼睛图标，默认全展示，可关闭或者打开（如果关闭对方将无法看到这条信息，包括打印和下载都不展示）</li><li>批量导入：可批量导入产品（先下载模板Excel进行编辑导入即可）</li><li>批量删除：勾选所需删除的产品可批量删除</li><li>使用提示：点击使用提示可弹出弹窗，在弹窗中显示动图，可切换查看对应功能的使用（新增字段、批量修改价格、调整宽度、签章显示、签章不显示）</li><li>历史报价：跟这个公司销售过这个产品，如果再次报价就会有之前的报价信息</li><li>历史成交价：跟这个公司销售过这个产品,成功了以后再次销售会有之前的历史成交价</li><li>总计：是所添加的产品单价的总计(支持更改单价添加公式(加 , 减 , 乘(乘的是百分比也就是0.01) 输入数值)</li><li>字段更改：在添加产品信息后可以悬浮对应的产品信息如果出现&quot;更改的图标&quot;就代表可以更改这个产品内容的信息,或者出现时双击也可以更改</li><li>交货时间：添加交货时间，不添加交货时间在签章时会提醒</li><li>合同附件：点击可上传合同附件</li><li>抄送：下方文本框内所填写的信息会发送给所勾选的抄送对象</li><li>存为模板：如果点击存为模板在模板列表中展示所存的模板</li><li>签章：如果点击签章在选择合同章确认后,在合同列表显示，可开始生产</li><li>保存：点击保存,在合同列表显示状态为 &quot;未签章&quot;，点击详情可进去签章</li></ul><p><img src="'+t+'" alt="如图所示"></p>',25)]))}const g=o(s,[["render",c]]);export{x as __pageData,g as default};
